mapper.select:
	select pms_commodity.id
	from pms_commodity 
	where pms_commodity.category_code = #{category.categoryCode}

         AND commodity_code like #{commodityCode,jdbcType=VARCHAR}

	 order by id desc

		select pms_commodity.id
		from pms_commodity 
		where 1=1

         AND pms_commodity.category_code like #{category.categoryCode}


         AND pms_commodity.commodity_name like #{commodityName,jdbcType=VARCHAR}


         AND commodity_code like #{commodityCode,jdbcType=VARCHAR}


         AND commodity_status = #{commodityStatus,jdbcType=INTEGER}


         AND commodity_show_place = #{commodityShowPlace,jdbcType=INTEGER}

		 order by id desc

SELECT pms_commodity.id, pms_commodity.commodity_code, pms_commodity.commodity_name, pms_commodity.commodity_status, pms_commodity.product_code, pms_product.product_name, pms_product_category.category_code, pms_product.product_buy_unit_code, pms_product.product_buy_unit_name, pms_product.product_specification_value, pms_product.product_specification_unit_first, pms_product.product_specification_unit_first_value, pms_product.product_specification_unit_second, pms_product.product_specification_unit_second_value, pms_product.product_temperature_zone_name, pms_product.product_temperature_zone_code, pms_commodity.commodity_comment, pms_commodity.commodity_price, pms_commodity.commodity_sales_price, pms_commodity.commodity_weight_unit, pms_commodity.commodity_product_quantity, pms_commodity.commodity_gs1_code, pms_product_category.category_name, pms_commodity.commodity_weight, pms_commodity.commodity_quantity, pms_commodity.commodity_show_place, pms_commodity.commodity_city, pms_commodity_property.id AS cid, pms_commodity_property.commdity_property_name, pms_commodity_property.commdity_property_value, pms_commodity_picture.picture_url AS image_url, pms_product.product_status FROM pms_commodity LEFT JOIN pms_commodity_picture ON pms_commodity_picture.commodity_code = pms_commodity.commodity_code LEFT JOIN pms_commodity_property ON pms_commodity_property.commdity_code = pms_commodity.commodity_code LEFT JOIN pms_product_category ON pms_commodity.category_code = pms_product_category.category_code LEFT JOIN pms_product ON pms_commodity.product_code = pms_product.product_code WHERE pms_commodity_picture.picture_show_order = 0 AND pms_commodity_picture.picture_show_position = 0 AND pms_commodity.id IN
#{item}
order by pms_commodity.id desc

mapper.update:
    update pms_commodity set


	            commodity_status = #{item.state}  


     where id in 

			#{item.id}

mapper.select:
SELECT pms_commodity.id, pms_commodity.commodity_code, pms_commodity.commodity_name, pms_commodity.commodity_status, pms_commodity.product_code, pms_commodity.commodity_comment, pms_commodity.commodity_price, pms_commodity.commodity_sales_price, pms_commodity.commodity_weight_unit, pms_commodity.commodity_product_quantity, pms_commodity.commodity_gs1_code, pms_product_category.category_name, pms_commodity.commodity_weight, pms_commodity.commodity_quantity, pms_commodity.commodity_show_place, pms_commodity.commodity_city FROM pms_commodity LEFT JOIN pms_product_category ON pms_commodity.category_code = pms_product_category.category_code WHERE 1=1
AND pms_commodity.category_code = #{category.categoryCode,jdbcType=VARCHAR}
AND commodity_status = #{commodityStatus,jdbcType=INTEGER}
AND commodity_show_place = #{commodityShowPlace,jdbcType=VARCHAR}
ORDER BY id DESC
