SELECT a.product_code AS item_code, a.product_name AS item_name, a.category_code AS item_category_code, b.category_name AS item_category_name, a.product_specification_value AS item_specification_value, a.product_specification_unit_first AS item_specification_unit_first, a.product_specification_unit_first_value AS item_specification_unit_first_value, a.product_specification_unit_second AS item_specification_unit_second, a.product_specification_unit_second_value AS item_specification_unit_second_value, a.product_buy_unit_name AS item_buy_unit_name, a.product_buy_unit_code AS item_buy_unit_value, a.product_temperature_zone_name AS item_temperature_zone_name, a.product_temperature_zone_code AS item_temperature_zone_code, a.tax_rate_name AS item_tax_rate_name, a.tax_rate_value AS item_tax_rate_value, 0 AS data_source, 1 AS item_type FROM pms_product AS a JOIN pms_product_category AS b ON a.category_code = b.category_code WHERE a.product_code in
#{item}
ORDER BY a.product_code ASC

SELECT c.commodity_code AS item_code, c.commodity_name AS item_name, c.category_code AS item_category_code, d.category_name AS item_category_name, NULL AS item_specification_value, c.commodity_weight_unit AS item_specification_unit_first, NULL AS item_specification_unit_first_value, NULL AS item_specification_unit_second_value, NULL AS item_specification_unit_second, NULL AS item_buy_unit_name, NULL AS item_buy_unit_value, c.product_temperature_zone_name AS item_temperature_zone_name, c.product_temperature_zone_code AS item_temperature_zone_code, c.tax_rate_name AS item_tax_rate_name, c.tax_rate_value AS item_tax_rate_value, 0 AS data_source, 0 AS item_type FROM pms_commodity AS c JOIN pms_product_category AS d ON c.category_code = d.category_code WHERE c.commodity_code in
#{item}
ORDER BY c.commodity_code ASC
